def is_anagram(str1, str2):
    """
    Checks if two strings are anagrams of one another
    """
    if len(str1) != len(str2):
        return False

    str1_counts = {}
    for char in str1:
        if char in str1_counts:
            str1_counts[char] += 1
        else:
            str1_counts[char] = 1

    str2_counts = {}
    for char in str2:
