def list_paths(tree, path=[]):
    all_paths = []
    head = list(tree.keys())[0]
    tail = tree[head]

    if path: 
        cur_path = path + [head] 
    else: 
        cur_path = [head] 

    if not tail: 
        all_paths.append(cur_path) 
    else:
        for node in tail.keys():
