def create_tree(incidence_list):
    """
    Generates a tree from the given incidence list.
    @param incidence_list: List
    @return: tuple
    """
    tree = {}
    for item in incidence_list:
        parent, child = item
        if parent in tree:
            tree[parent].append(child)
        else:
            tree[parent] = [child]
    return tree
