def evaluate(expression): 
    expression = expression.replace(" ", "")
    tokens = list(expression)

    a = tokens.pop(0)
    while len(tokens) > 0: 
        op = tokens.pop(0)
        b = tokens.pop(0)
        if op == '+':
            a = int(a) + int(b)
        elif op == '-':
            a = int(a) - int(b)
        elif op == '*':
            a = int(a) * int(b)
        elif op == '/':
            a = int(a) / int(b)
        else: 
            return False

    return a

