def sort(arr): 
    if len(arr) == 0: 
        return [] 
    else: 
        pivot = arr[0] 
        lesser = sort([x for x in arr[1:] if x < pivot])
        greater = sort([x for x in arr[1:] if x >= pivot])
        return lesser + [pivot] + greater
