 if (0 <= new_x < GRID_SIZE) and (0 <= new_y < GRID_SIZE) and (new_x, new_y) not in obstacles:
 neighbors.append((new_x, new_y))

 return neighbors

def reconstruct_path(current_node):
 path = []

 while current_node is not None:
 current_node = parents[current_node]
 if current_node is not None:
 path.append(current_node)

 return path


def a_star(start_position, goal_position, obstacles):
 parents = {}
 visited = set() 
 open_set = set()
 open_set.add(start_position)

 while open_set:
 current_position = min(open_set, key=lambda pos: heuristic(pos, goal_position))
 visited.add(current_position)

 if current_position == goal_position:
 return reconstruct_path(current_position)

 for neighbor in get_valid_neighbors(current_position):
 gScore = current_position[0] + 1 
 if neighbor in open_set:
 if gScore < gScore[neighbor]: 
 parents[neighbor] = current_position 
 else:
 open_set.add(neighbor)
 parents[neighbor] = current_position

 return None
