def rgb2hsl(r, g, b):
 r /= 255
 g /= 255
 b /= 255

 max_num = max(r, g, b)
 min_num = min(r, g, b)

 h = s = l = (max_num + min_num) / 2

 if max_num == min_num:
 h = 0
 s = 0
 else:
 diff = max_num - min_num

 if l > 0.5:
 s = diff / (2 - max_num - min_num)
 else:
 s = diff / (max_num + min_num)

 if max_num == r:
 h = (g - b) / diff
 elif max_num == g:
 h = (2 + (b - r) / diff)
 else:
 h = (4 + (r - g) / diff)

 h *= 60
 if h < 0:
 h += 360

 return (h, s, l)

