def validate_inputs(input_ranges):
    """ This function validates the range inputs are correctly formatted as a list of tuples """
    if not isinstance(input_ranges, list):
        return False
    for range_input in input_ranges:
        if not isinstance(range_input, tuple) or not len(range_input) == 2:
