def createTree(arr):
    root = Node(arr[0])
    for i in arr[1:]:
        current_node = root
        while True:
            if i <= current_node.data:
                if current_node.left is None:
                    current_node.left = Node(i)
                    break
                else:
                    current_node = current_node.left
            elif i > current_node.data:
