def sort_array(A):
    if len(A) <= 1:
        return A

    less = []
    equal = []
    greater = []

    pivot = A[0]
    for num in A:
        if num < pivot:
            less.append(num)
        elif num == pivot:
            equal.append(num)
        elif num > pivot:
            greater.append(num)

    return sort_array(less) + equal + sort_array(greater)
