 self.rows = rows
 self.cols = cols
 self.matrix = [[0 for i in range(cols)] for j in range(rows)]

def create_matrix(rows, cols, values):            
 m = Matrix(rows, cols)
 for i in range(rows):
 for j in range(cols):
 m.matrix[i][j] = values[i][j]
 return m

def mult_matrix(m1, m2): 
 m = Matrix(m1.rows, m2.cols)
 for i in range(m1.rows):
 for j in range(m2.cols):
 for k in range(m1.cols):
 m.matrix[i][j] += m1.matrix[i][k] * m2.matrix[k][j]
 return m

def analyze_matrix(m):
 stats = defaultdict(int)
 for row in range(m.rows):
 for col in range(m.cols):
 stats[m.matrix[row][col]] += 1
 return stats
