class DoublyLinkedListNode:
    def __init__(self, data):
        self.data = data
        self.prev = None 
        self.next = None

class DoublyLinkedList:
    def __init__(self):
        self.head = None

    def append(self, data):
        if self.head is None:
            new_node = DoublyLinkedListNode(data)
            new_node.prev = None
            self.head = new_node
        else:
            new_node = DoublyLinkedListNode(data)
            cur = self.head
            while cur.next:
                cur = cur.next
            cur.next = new_node
            new_node.prev = cur 
