def permute(nums):
  res = [] 
  backtrack(nums, res, [])
  return res

def backtrack(nums, res, temp):
  if(len(temp) == len(nums)):
    res.append(temp[:])
  else:
    for num in nums:
      if num not in temp:
        temp.append(num)
        backtrack(nums, res, temp)
        temp.pop()

