class Matrix:
 def __init__(self, matrix):
 self.matrix = matrix

def __add__(self, other):
 if len(self.matrix) != len(other.matrix):
 raise ValueError("The matrixes must have the same size!")
 result = [[0 for x in range(len(self.matrix[0]))] for y in range(len(self.matrix))]
 for i in range(len(self.matrix)):
 for j in range(len(self.matrix[i])):
 result[i][j] = self.matrix[i][j] + other.matrix[i][j]
 return result

def __mul__(self, other):
 if len(self.matrix[0]) != len(other.matrix):
 raise ValueError("The matrixes' columns of the first matrix must match the rows of the second matrix!")
 result = [[0 for x in range(len(other.matrix[0]))] for y in range(len(self.matrix))]
 for i in range(len(self.matrix)):
 for j in range(len(other.matrix[0])):
 for k in range(len(other.matrix)):
 result[i][j] += self.matrix[i][k] * other.matrix[k][j]
 return result
