def search(string, search): 
	# Create a Trie type datastructure 
	t = {}

	# Put each character of the string in a lexicographic tree 
	for char in string: 
		if char in t: 
			t = t[char] 
		else: 
			t[char] = {}
			t = t[char] 

	# Now search for the search string in the trie structure 
