        if cell == ' ':
            return ' '
        if cell == '.':
            return '.'

    # Render the picture as ASCII
    result = ''
    for row in range(height):
        for col in range(width):
            if row == 0 or row == height - 1 or col == 0 or col == width - 1:
                result += '*'
            else:
                result += get_ascii_cell(drawing[row-1][col-1])
        result += '\n'
    return result

if __name__ == '__main__':
    drawing = [['*', ' ', '*'],
               [' ', '*', ' '],
               ['*', ' ', '*']]
