def check_duplicates_replace(list):
    # keep track of counts of elements
    element_count = {}

    # traverse list
    for element in list:
        if element in element_count:
            element_count[element] += 1
        else:
            element_count[element] = 1

    # replace duplicates with count
    for i in range(len(list)):
        if list[i] in element_count:
            list[i] = element_count[list[i]]
